import React from "react";
import { Grid, Paper, Typography } from "@material-ui/core";
import PokeCard from "./PokeCard";
import { withStyles } from "@material-ui/core/styles";

function PokeDescription({ pokeImage, name, description, classes }) {
  return (
    <>
      <Grid container className={classes.centerContainer}>
        <Grid item className={classes.imageFull} md={6}>
          <PokeCard
            className={classes.imageFull}
            to=""
            image={pokeImage}
            name={name}
          />
        </Grid>
        <Grid item md={6}>
          <div className={classes.descriptionContainer}>
            <Paper className={classes.descriptionBox}>
              <Typography
                className={classes.descriptionText}
                variant="body1"
                component="p"
              >
                {description}
              </Typography>
            </Paper>
          </div>
        </Grid>
      </Grid>
    </>
  );
}

export default withStyles({
  centerContainer: {
    margin: "0 auto",
    width: "60% !important"
  },
  descriptionContainer: {
    padding: "2em"
  },
  descriptionBox: {
    padding: "2em",
    height: "auto"
  },
  descriptionText: {
    fontSize: "1em",
    textAlign: "justify",
    fontFamily: "Verdana"
  },
  imageFull: {
    minHeight: "300px",
    minWidth: "300px"
  }
})(PokeDescription);
