import React, { Component, useState, useEffect } from "react";
import axios from "axios";
import List from "../components/List";
import AppNav from "../components/AppNav";
import Footer from "../components/Footer";
import Posts from "../components/Posts";
import Pagination from "../components/Pagination";
import { Box } from "@material-ui/core";

const PokeListContainer = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(10);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      const res = await axios.get("https://pokeapi.co/api/v2/pokemon?offset=0");
      setPosts(res.data);
      setLoading(false);
    };

    fetchPosts();
  }, []);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = pageNumber => setCurrentPage(pageNumber);

  class PokeListContainer extends Component {
    state = {
      pokeData: []
    };

    componentDidMount() {
      axios
        .get("https://pokeapi.co/api/v2/pokemon?offset=0")
        .then(res => {
          const pokeData = res.data.results;
          this.setState({ pokeData });
        })
        .catch(err => {
          console.log(err);
        });
    }

    render() {
      const { pokeData } = this.state;
      return (
        <>
          <div className="Background-Img2">
            <AppNav />
            <Box className="CardOpacity">
              <List pokedata={pokeData} />
            </Box>
            <Posts posts={currentPosts} loading={loading} />
            <Pagination
              postsPerPage={postsPerPage}
              totalPosts={posts.length}
              paginate={paginate}
            />
            <Footer />
          </div>
        </>
      );
    }
  }
};
export default PokeListContainer;
