import React, { Component } from "react";
import PokeDescription from "../components/PokeDescription";
import axios from "axios";
import AppNav from "../components/AppNav";
import Footer from "../components/Footer";

class PokeInfoContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pokemonDescription: "",
      pokeName: "",
      pokeId: ""
    };
  }

  componentDidMount() {
    //peticion con axios
    const { match } = this.props;
    const pokeName = match.params.pokeName;
    const pokeId = match.params.pokeIndex;
    const pokeDescriptionUrl = `${process.env.REACT_APP_POKE_API_BASE_URL}pokemon-species/${pokeId}`;
    axios.get(pokeDescriptionUrl).then(res => {
      const { flavor_text_entries } = res.data;
      let es;
      if ((flavor_text_entries[3].language = "es")) {
        es = flavor_text_entries[3].flavor_text;
      } else if ((flavor_text_entries[6].language = "es")) {
        es = flavor_text_entries[6].flavor_text;
      }
      this.setState({
        pokemonDescription: es,
        pokeName,
        pokeId
      });
      console.log(this.state.pokemonDescription);
    });
  }
  render() {
    let url = `${process.env.REACT_APP_POKEMON_ART}`;
    const { pokemonDescription, pokeName, pokeId } = this.state;
    return (
      <>
        <AppNav />
        <PokeDescription
          name={pokeName}
          pokeImage={`${url}${pokeId}.png?raw=true`}
          description={pokemonDescription}
        />
        <Footer></Footer>
      </>
    );
  }
}
export default PokeInfoContainer;
